import React from 'react';
import { useParams } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import useProducts from '../hooks/useProducts';

const ProductDetail = () => {
  const { id } = useParams();
  const { getProductById } = useProducts();
  const { addToCart } = useCart();

  const product = getProductById(id);

  if (!product) {
    return <div style={{ padding: '20px' }}><h2>Producto no encontrado</h2></div>;
  }

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '30px' }}>
        {/* Columna izquierda - Imagen */}
        <div style={{ flex: '1', minWidth: '300px' }}>
          <div style={{
            backgroundColor: '#f8f9fa',
            padding: '20px',
            borderRadius: '10px',
            textAlign: 'center'
          }}>
            <img 
              src={`https://via.placeholder.com/400x300/3498db/ffffff?text=${encodeURIComponent(product.name)}`}
              alt={product.name}
              style={{ 
                width: '100%', 
                maxHeight: '300px', 
                objectFit: 'contain',
                borderRadius: '8px'
              }}
            />
          </div>
        </div>

        {/* Columna derecha - Informaci√≥n */}
        <div style={{ flex: '1', minWidth: '300px' }}>
          <h1 style={{ marginBottom: '10px', color: '#2c3e50' }}>{product.name}</h1>

          <div style={{ 
            backgroundColor: '#e8f4fc', 
            padding: '10px 15px', 
            borderRadius: '6px',
            marginBottom: '15px',
            display: 'inline-block'
          }}>
            <span style={{ color: '#3498db', fontWeight: 'bold' }}>
              üìÅ {product.category}
            </span>
          </div>

          <p style={{ color: '#666', fontSize: '1rem' }}>
            SKU: <strong>{product.sku}</strong>
          </p>

          {/* ¬°AQU√ç FALTA EL PRECIO! */}
          <div style={{ 
            backgroundColor: '#fff8e1', 
            padding: '15px', 
            borderRadius: '8px',
            margin: '20px 0',
            borderLeft: '4px solid #f39c12'
          }}>
            <p style={{ 
              fontSize: '2.5rem', 
              color: '#e74c3c', 
              fontWeight: 'bold', 
              margin: '0'
            }}>
              ${product.price.toFixed(2)} <span style={{ fontSize: '1rem', color: '#666' }}>MXN</span>
            </p>
            <p style={{ color: '#27ae60', marginTop: '5px', fontSize: '0.9rem' }}>
              üí∞ Precio disponible
            </p>
          </div>

          <button 
            onClick={() => addToCart(product)}
            style={{
              background: 'linear-gradient(135deg, #3498db, #2980b9)',
              color: 'white',
              padding: '15px 30px',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer',
              fontSize: '1.2rem',
              margin: '20px 0',
              width: '100%',
              fontWeight: 'bold'
            }}
          >
            üõí A√±adir al carrito - ${product.price.toFixed(2)} MXN
          </button>

          <div style={{ 
            backgroundColor: '#f8f9fa', 
            padding: '20px', 
            borderRadius: '8px',
            marginTop: '30px'
          }}>
            <h3 style={{ marginBottom: '15px', color: '#2c3e50' }}>
              üìã Descripci√≥n del Producto
            </h3>
            <p style={{ 
              lineHeight: '1.6', 
              fontSize: '1.1rem',
              color: '#34495e'
            }}>
              Este {product.name} es un producto de alta calidad en la categor√≠a {product.category}. 
              Con SKU {product.sku}, est√° disponible por solo <strong>${product.price.toFixed(2)} MXN</strong>.
              Perfecto para tus necesidades tecnol√≥gicas.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
